import React, { useEffect, useState } from 'react';
import { useReviewStore } from '../store/reviewStore';
import RatingStars from './RatingStars';
import Pagination from './Pagination';
import { apiUtils } from '../api';

interface ReviewListProps {
  embedded?: boolean;
}

const ReviewList: React.FC<ReviewListProps> = ({ embedded = false }) => {
  const {
    reviews,
    loading,
    error,
    pagination,
    filters,
    fetchReviews,
    setPage,
    clearError,
    updateFilters,
  } = useReviewStore();

  // Local state for filter controls
  const [searchTerm, setSearchTerm] = useState(filters.search || '');
  const [selectedRating, setSelectedRating] = useState<number | undefined>(filters.rating);
  const [showFeaturedOnly, setShowFeaturedOnly] = useState(filters.featured_only || false);
  const [sortBy, setSortBy] = useState(filters.sort_by || 'created_at');
  const [sortOrder, setSortOrder] = useState(filters.sort_order || 'desc');

  useEffect(() => {
    fetchReviews();
  }, [fetchReviews]);

  // Handler f√ºr Filter-Updates
  const handleFilterUpdate = () => {
    const newFilters = {
      search: searchTerm || undefined,
      rating: selectedRating || undefined,
      featured_only: showFeaturedOnly || undefined,
      sort_by: sortBy,
      sort_order: sortOrder,
    };
    updateFilters(newFilters);
  };

  // Filter zur√ºcksetzen
  const resetFilters = () => {
    setSearchTerm('');
    setSelectedRating(undefined);
    setShowFeaturedOnly(false);
    setSortBy('created_at');
    setSortOrder('desc');
    updateFilters({
      search: undefined,
      rating: undefined,
      featured_only: undefined,
      sort_by: 'created_at',
      sort_order: 'desc',
    });
  };

  if (loading && reviews.length === 0) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="spinner" />
        <span className="ml-2 text-gray-600">Bewertungen werden geladen...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">
              Fehler beim Laden der Bewertungen
            </h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
            <div className="mt-4">
              <button
                type="button"
                className="bg-red-100 px-2 py-1 text-sm text-red-800 rounded hover:bg-red-200"
                onClick={() => {
                  clearError();
                  fetchReviews();
                }}
              >
                Erneut versuchen
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (reviews.length === 0) {
    return (
      <div className="text-center py-12">
        <svg
          className="mx-auto h-12 w-12 text-gray-400"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-1l-4 4z"
          />
        </svg>
        <h3 className="mt-2 text-sm font-medium text-gray-900">
          Keine Bewertungen vorhanden
        </h3>
        <p className="mt-1 text-sm text-gray-500">
          Seien Sie der erste, der eine Bewertung abgibt!
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filter Controls (nur wenn nicht embedded) */}
      {!embedded && (
        <div className="bg-gradient-to-br from-white to-gray-50 p-8 rounded-2xl border border-gray-200 shadow-lg backdrop-blur-sm">
          <div className="flex items-center mb-6">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
                <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
                </svg>
              </div>
              <div>
                <h3 className="text-xl font-semibold text-gray-900">Bewertungen filtern</h3>
                <p className="text-sm text-gray-600">Finden Sie genau die Bewertungen, die Sie suchen</p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6 mb-8">
            {/* Suchfeld */}
            <div className="space-y-2">
              <label htmlFor="search" className="block text-sm font-semibold text-gray-700">
                üîç Suche
              </label>
              <div className="relative">
                <input
                  type="text"
                  id="search"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Name oder Kommentar durchsuchen..."
                  className="w-full pl-4 pr-10 py-3 bg-white border border-gray-300 rounded-xl focus:outline-none focus:ring-3 focus:ring-blue-500/20 focus:border-blue-500 transition-all duration-200 shadow-sm hover:shadow-md"
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
              </div>
            </div>

            {/* Bewertung filtern */}
            <div className="space-y-2">
              <label htmlFor="rating" className="block text-sm font-semibold text-gray-700">
                ‚≠ê Mindest-Bewertung
              </label>
              <select
                id="rating"
                value={selectedRating || ''}
                onChange={(e) => setSelectedRating(e.target.value ? parseInt(e.target.value) : undefined)}
                className="w-full px-4 py-3 bg-white border border-gray-300 rounded-xl focus:outline-none focus:ring-3 focus:ring-blue-500/20 focus:border-blue-500 transition-all duration-200 shadow-sm hover:shadow-md appearance-none cursor-pointer"
              >
                <option value="">Alle Bewertungen anzeigen</option>
                <option value="5">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê Exzellent (5 Sterne)</option>
                <option value="4">‚≠ê‚≠ê‚≠ê‚≠ê Sehr gut (4+ Sterne)</option>
                <option value="3">‚≠ê‚≠ê‚≠ê Gut (3+ Sterne)</option>
                <option value="2">‚≠ê‚≠ê Befriedigend (2+ Sterne)</option>
                <option value="1">‚≠ê Alle Bewertungen (1+ Stern)</option>
              </select>
            </div>

            {/* Sortierung */}
            <div className="space-y-2">
              <label htmlFor="sort" className="block text-sm font-semibold text-gray-700">
                üìä Sortierung
              </label>
              <select
                id="sort"
                value={`${sortBy}-${sortOrder}`}
                onChange={(e) => {
                  const [by, order] = e.target.value.split('-');
                  setSortBy(by as 'created_at' | 'rating' | 'name');
                  setSortOrder(order as 'asc' | 'desc');
                }}
                className="w-full px-4 py-3 bg-white border border-gray-300 rounded-xl focus:outline-none focus:ring-3 focus:ring-blue-500/20 focus:border-blue-500 transition-all duration-200 shadow-sm hover:shadow-md appearance-none cursor-pointer"
              >
                <option value="created_at-desc">üïí Neueste zuerst</option>
                <option value="created_at-asc">‚è∞ √Ñlteste zuerst</option>
                <option value="rating-desc">üåü Beste Bewertung zuerst</option>
                <option value="rating-asc">üìà Schlechteste zuerst</option>
                <option value="name-asc">üî§ Name A-Z</option>
                <option value="name-desc">üî° Name Z-A</option>
              </select>
            </div>

            {/* Featured Only */}
            <div className="space-y-2">
              <label className="block text-sm font-semibold text-gray-700">
                üèÜ Spezialfilter
              </label>
              <div className="bg-white border border-gray-300 rounded-xl p-4 shadow-sm hover:shadow-md transition-all duration-200">
                <div className="flex items-center space-x-3">
                  <div className="relative">
                    <input
                      id="featured"
                      type="checkbox"
                      checked={showFeaturedOnly}
                      onChange={(e) => setShowFeaturedOnly(e.target.checked)}
                      className="w-5 h-5 text-blue-600 bg-white border-2 border-gray-300 rounded focus:ring-3 focus:ring-blue-500/20 focus:border-blue-500 transition-all duration-200"
                    />
                    {showFeaturedOnly && (
                      <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                        <svg className="w-3 h-3 text-blue-600" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                        </svg>
                      </div>
                    )}
                  </div>
                  <div>
                    <label htmlFor="featured" className="text-sm font-medium text-gray-700 cursor-pointer">
                      Nur empfohlene Bewertungen
                    </label>
                    <p className="text-xs text-gray-500 mt-1">Premium-Bewertungen anzeigen</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Statistiken und Aktionen */}
          <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-4 pt-6 border-t border-gray-200">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span className="font-medium text-gray-700">
                  {reviews.length} von {pagination.total} Bewertungen
                </span>
              </div>
              {(searchTerm || selectedRating || showFeaturedOnly) && (
                <div className="flex items-center space-x-1 text-xs text-blue-600 bg-blue-50 px-2 py-1 rounded-full">
                  <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clipRule="evenodd" />
                  </svg>
                  <span>Filter aktiv</span>
                </div>
              )}
            </div>

            <div className="flex space-x-3">
              <button
                onClick={resetFilters}
                className="group px-6 py-3 text-sm font-medium text-gray-700 bg-white border-2 border-gray-300 rounded-xl hover:bg-gray-50 hover:border-gray-400 focus:outline-none focus:ring-3 focus:ring-gray-500/20 transition-all duration-200 flex items-center space-x-2"
              >
                <svg className="w-4 h-4 group-hover:rotate-180 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
                <span>Zur√ºcksetzen</span>
              </button>
              <button
                onClick={handleFilterUpdate}
                className="group px-8 py-3 text-sm font-semibold text-white bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl hover:from-blue-700 hover:to-purple-700 focus:outline-none focus:ring-3 focus:ring-blue-500/20 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 flex items-center space-x-2"
              >
                <svg className="w-4 h-4 group-hover:scale-110 transition-transform duration-200" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
                <span>Filter anwenden</span>
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Reviews */}
      <div className="space-y-4">
        {reviews.map((review) => (
          <div
            key={review.id}
            className={`review-card ${review.is_featured ? 'featured' : ''}`}
          >
            {/* Header */}
            <div className="flex items-start justify-between mb-3">
              <div>
                <h3 className="font-semibold text-gray-900">{review.name}</h3>
                {review.title && (
                  <h4 className="text-sm text-gray-600 mt-1">{review.title}</h4>
                )}
              </div>
              {review.is_featured && (
                <span className="badge badge-warning">Empfohlen</span>
              )}
            </div>

            {/* Rating */}
            <div className="mb-3">
              <RatingStars rating={review.rating} />
            </div>

            {/* Content */}
            <div className="text-gray-700 mb-4">
              <p className="whitespace-pre-wrap">
                {embedded 
                  ? apiUtils.truncateText(review.content, 150)
                  : review.content
                }
              </p>
            </div>

            {/* Image */}
            {review.image_path && (
              <div className="mb-4">
                <img
                  src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${review.image_path}`}
                  alt="Bewertungsbild"
                  className="max-w-full h-48 object-cover rounded-lg border border-gray-300"
                />
              </div>
            )}

            {/* Footer */}
            <div className="flex items-center justify-between text-sm text-gray-500 border-t border-gray-200 pt-3">
              <time dateTime={review.created_at}>
                {apiUtils.formatDate(review.created_at)}
              </time>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination (nur wenn nicht embedded) */}
      {!embedded && pagination.total_pages > 1 && (
        <Pagination
          currentPage={pagination.page}
          totalPages={pagination.total_pages}
          onPageChange={setPage}
        />
      )}

      {/* Loading overlay f√ºr zus√§tzliche Seiten */}
      {loading && reviews.length > 0 && (
        <div className="flex justify-center py-4">
          <div className="spinner" />
          <span className="ml-2 text-gray-600">L√§dt...</span>
        </div>
