import React, { useEffect, useState } from 'react';
import { useReviewStore } from '../store/reviewStore';
import RatingStars from './RatingStars';
import Pagination from './Pagination';
import { apiUtils } from '../api';

interface ReviewListProps {
  embedded?: boolean;
}

const ReviewList: React.FC<ReviewListProps> = ({ embedded = false }) => {
  const {
    reviews,
    loading,
    error,
    pagination,
    filters,
    fetchReviews,
    setPage,
    clearError,
    updateFilters,
  } = useReviewStore();

  // Local state for filter controls
  const [searchTerm, setSearchTerm] = useState(filters.search || '');
  const [selectedRating, setSelectedRating] = useState<number | undefined>(filters.rating);
  const [showFeaturedOnly, setShowFeaturedOnly] = useState(filters.featured_only || false);
  const [sortBy, setSortBy] = useState(filters.sort_by || 'created_at');
  const [sortOrder, setSortOrder] = useState(filters.sort_order || 'desc');

  useEffect(() => {
    fetchReviews();
  }, [fetchReviews]);

  // Handler für Filter-Updates
  const handleFilterUpdate = () => {
    const newFilters = {
      search: searchTerm || undefined,
      rating: selectedRating || undefined,
      featured_only: showFeaturedOnly || undefined,
      sort_by: sortBy,
      sort_order: sortOrder,
    };
    updateFilters(newFilters);
  };

  // Filter zurücksetzen
  const resetFilters = () => {
    setSearchTerm('');
    setSelectedRating(undefined);
    setShowFeaturedOnly(false);
    setSortBy('created_at');
    setSortOrder('desc');
    updateFilters({
      search: undefined,
      rating: undefined,
      featured_only: undefined,
      sort_by: 'created_at',
      sort_order: 'desc',
    });
  };

  if (loading && reviews.length === 0) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="spinner" />
        <span className="ml-2 text-gray-600">Bewertungen werden geladen...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">
              Fehler beim Laden der Bewertungen
            </h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
            <div className="mt-4">
              <button
                type="button"
                className="bg-red-100 px-2 py-1 text-sm text-red-800 rounded hover:bg-red-200"
                onClick={() => {
                  clearError();
                  fetchReviews();
                }}
              >
                Erneut versuchen
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (reviews.length === 0) {
    return (
      <div className="text-center py-12">
        <svg
          className="mx-auto h-12 w-12 text-gray-400"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-1l-4 4z"
          />
        </svg>
        <h3 className="mt-2 text-sm font-medium text-gray-900">
          Keine Bewertungen vorhanden
        </h3>
        <p className="mt-1 text-sm text-gray-500">
          Seien Sie der erste, der eine Bewertung abgibt!
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filter Controls (nur wenn nicht embedded) */}
      {!embedded && (
        <div className="bg-white rounded-lg border border-gray-200 shadow-sm">
          {/* Compact Filter Header */}
          <div className="flex items-center justify-between px-4 py-3 border-b border-gray-100 bg-gray-50/80">
            <div className="flex items-center space-x-2">
              <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.207A1 1 0 013 6.5V4z" />
              </svg>
              <h3 className="text-sm font-medium text-gray-700">Filter & Sortierung</h3>
            </div>

            {/* Status Badge */}
            <div className="flex items-center space-x-3">
              {(searchTerm || selectedRating || showFeaturedOnly) && (
                <div className="flex items-center space-x-1 text-xs text-amber-700 bg-amber-100 px-2 py-1 rounded-full">
                  <div className="w-2 h-2 bg-amber-500 rounded-full"></div>
                  <span className="font-medium">Aktiv</span>
                </div>
              )}
              <div className="text-xs text-gray-500 font-medium">
                {reviews.length} von {pagination.total}
              </div>
            </div>
          </div>

          {/* Compact Filter Body */}
          <div className="p-4">
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-3 items-end">
              {/* Search Input - Compact */}
              <div className="lg:col-span-4">
                <div className="relative">
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Name oder Kommentar durchsuchen..."
                    className="w-full pl-8 pr-3 py-2 text-sm bg-white border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-150"
                  />
                  <div className="absolute inset-y-0 left-0 pl-2.5 flex items-center pointer-events-none">
                    <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Rating Filter - Compact */}
              <div className="lg:col-span-3">
                <div className="relative">
                  <select
                    value={selectedRating || ''}
                    onChange={(e) => setSelectedRating(e.target.value ? parseInt(e.target.value) : undefined)}
                    className="w-full pl-3 pr-8 py-2 text-sm bg-white border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-150 cursor-pointer appearance-none"
                  >
                    <option value="">Alle Bewertungen</option>
                    <option value="5">⭐⭐⭐⭐⭐ 5 Sterne</option>
                    <option value="4">⭐⭐⭐⭐ 4+ Sterne</option>
                    <option value="3">⭐⭐⭐ 3+ Sterne</option>
                    <option value="2">⭐⭐ 2+ Sterne</option>
                    <option value="1">⭐ 1+ Stern</option>
                  </select>
                  <div className="absolute inset-y-0 right-0 pr-2 flex items-center pointer-events-none">
                    <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Sort Filter - Compact */}
              <div className="lg:col-span-3">
                <div className="relative">
                  <select
                    value={`${sortBy}-${sortOrder}`}
                    onChange={(e) => {
                      const [by, order] = e.target.value.split('-');
                      setSortBy(by as 'created_at' | 'rating' | 'name');
                      setSortOrder(order as 'asc' | 'desc');
                    }}
                    className="w-full pl-3 pr-8 py-2 text-sm bg-white border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-150 cursor-pointer appearance-none"
                  >
                    <option value="created_at-desc">🕒 Neueste zuerst</option>
                    <option value="created_at-asc">⏰ Älteste zuerst</option>
                    <option value="rating-desc">🌟 Beste zuerst</option>
                    <option value="rating-asc">📈 Schlechteste zuerst</option>
                    <option value="name-asc">🔤 Name A-Z</option>
                    <option value="name-desc">🔡 Name Z-A</option>
                  </select>
                  <div className="absolute inset-y-0 right-0 pr-2 flex items-center pointer-events-none">
                    <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Action Buttons - Compact */}
              <div className="lg:col-span-2 flex space-x-2">
                <button
                  onClick={resetFilters}
                  className="flex-1 px-3 py-2 text-xs font-medium text-gray-600 bg-gray-100 border border-gray-200 rounded-md hover:bg-gray-200 hover:text-gray-700 focus:outline-none focus:ring-1 focus:ring-gray-400 transition-all duration-150 flex items-center justify-center space-x-1"
                  title="Filter zurücksetzen"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                  </svg>
                  <span className="hidden sm:inline">Reset</span>
                </button>
                <button
                  onClick={handleFilterUpdate}
                  className="flex-1 px-3 py-2 text-xs font-medium text-white bg-blue-600 border border-blue-600 rounded-md hover:bg-blue-700 hover:border-blue-700 focus:outline-none focus:ring-1 focus:ring-blue-500 transition-all duration-150 flex items-center justify-center space-x-1 shadow-sm"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                  <span className="hidden sm:inline">Filter</span>
                </button>
              </div>
            </div>

            {/* Special Filter Row - Compact */}
            {!embedded && (
              <div className="mt-3 pt-3 border-t border-gray-100">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <input
                      id="featured"
                      type="checkbox"
                      checked={showFeaturedOnly}
                      onChange={(e) => setShowFeaturedOnly(e.target.checked)}
                      className="w-4 h-4 text-blue-600 bg-white border-gray-300 rounded focus:ring-1 focus:ring-blue-500 transition-all duration-150"
                    />
                    <label htmlFor="featured" className="text-xs font-medium text-gray-600 cursor-pointer select-none">
                      Nur empfohlene Bewertungen
                    </label>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Reviews */}
      <div className="space-y-6">
        {reviews.map((review, index) => (
          <div
            key={review.id}
            className={`group relative bg-white rounded-2xl border transition-all duration-300 hover:shadow-xl hover:-translate-y-1 ${
              review.is_featured 
                ? 'border-amber-200 bg-gradient-to-br from-amber-50 to-orange-50 shadow-lg ring-2 ring-amber-100' 
                : 'border-gray-200 shadow-md hover:border-gray-300'
            }`}
          >
            {/* Featured Badge */}
            {review.is_featured && (
              <div className="absolute -top-3 -right-3 z-10">
                <div className="flex items-center space-x-1 bg-gradient-to-r from-amber-400 to-orange-500 text-white px-3 py-1 rounded-full text-xs font-semibold shadow-lg">
                  <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                  </svg>
                  <span>Empfohlen</span>
                </div>
              </div>
            )}

            <div className="p-6">
              {/* Header mit Avatar */}
              <div className="flex items-start space-x-4 mb-4">
                <div className="flex-shrink-0">
                  <div className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold text-lg ${
                    review.is_featured 
                      ? 'bg-gradient-to-br from-amber-400 to-orange-500' 
                      : 'bg-gradient-to-br from-blue-500 to-purple-600'
                  }`}>
                    {review.name.charAt(0).toUpperCase()}
                  </div>
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900 truncate">
                        {review.name}
                      </h3>
                      {review.title && (
                        <h4 className="text-sm text-gray-600 mt-0.5 font-medium">
                          {review.title}
                        </h4>
                      )}
                    </div>
                    <div className="flex-shrink-0 ml-4">
                      <div className="flex items-center space-x-2">
                        <div className="flex items-center">
                          <RatingStars rating={review.rating} size="sm" />
                        </div>
                        <span className="text-sm font-medium text-gray-600">
                          {review.rating}/5
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Content */}
              <div className="mb-4">
                <div className={`text-gray-700 leading-relaxed ${
                  review.is_featured ? 'text-gray-800' : ''
                }`}>
                  <p className="whitespace-pre-wrap">
                    {embedded 
                      ? apiUtils.truncateText(review.content, 150)
                      : review.content
                    }
                  </p>
                </div>
              </div>

              {/* Image */}
              {review.image_path && (
                <div className="mb-4">
                  <div className="relative overflow-hidden rounded-xl">
                    <img
                      src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${review.image_path}`}
                      alt="Bewertungsbild"
                      className="w-full h-48 object-cover transition-transform duration-300 group-hover:scale-105"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  </div>
                </div>
              )}

              {/* Footer */}
              <div className="flex items-center justify-between pt-4 border-t border-gray-200">
                <div className="flex items-center space-x-2 text-sm text-gray-500">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <time dateTime={review.created_at}>
                    {apiUtils.formatDate(review.created_at)}
                  </time>
                </div>

                {/* Review-Nummer für bessere Übersicht */}
                <div className="flex items-center space-x-2">
                  <span className="text-xs text-gray-400 font-medium">
                    #{String(index + 1).padStart(2, '0')}
                  </span>
                  <div className="w-2 h-2 rounded-full bg-gray-300"></div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination (nur wenn nicht embedded) */}
      {!embedded && pagination.total_pages > 1 && (
        <Pagination
          currentPage={pagination.page}
          totalPages={pagination.total_pages}
          onPageChange={setPage}
        />
      )}

      {/* Loading overlay für zusätzliche Seiten */}
      {loading && reviews.length > 0 && (
        <div className="flex justify-center py-4">
          <div className="spinner" />
          <span className="ml-2 text-gray-600">Lädt...</span>
        </div>
      )}
    </div>
  );
};

export default ReviewList;
