import React, { useEffect, useState } from 'react';
import { useReviewStore } from '../store/reviewStore';
import RatingStars from './RatingStars';
import Pagination from './Pagination';
import { apiUtils } from '../api';

interface ReviewListProps {
  embedded?: boolean;
}

const ReviewList: React.FC<ReviewListProps> = ({ embedded = false }) => {
  const {
    reviews,
    loading,
    error,
    pagination,
    filters,
    fetchReviews,
    setPage,
    clearError,
    updateFilters,
  } = useReviewStore();

  // Local state for filter controls
  const [searchTerm, setSearchTerm] = useState(filters.search || '');
  const [selectedRating, setSelectedRating] = useState<number | undefined>(filters.rating);
  const [showFeaturedOnly, setShowFeaturedOnly] = useState(filters.featured_only || false);
  const [sortBy, setSortBy] = useState(filters.sort_by || 'created_at');
  const [sortOrder, setSortOrder] = useState(filters.sort_order || 'desc');

  useEffect(() => {
    fetchReviews();
  }, [fetchReviews]);

  // Handler für Filter-Updates
  const handleFilterUpdate = () => {
    const newFilters = {
      search: searchTerm || undefined,
      rating: selectedRating || undefined,
      featured_only: showFeaturedOnly || undefined,
      sort_by: sortBy,
      sort_order: sortOrder,
    };
    updateFilters(newFilters);
  };

  // Filter zurücksetzen
  const resetFilters = () => {
    setSearchTerm('');
    setSelectedRating(undefined);
    setShowFeaturedOnly(false);
    setSortBy('created_at');
    setSortOrder('desc');
    updateFilters({
      search: undefined,
      rating: undefined,
      featured_only: undefined,
      sort_by: 'created_at',
      sort_order: 'desc',
    });
  };

  if (loading && reviews.length === 0) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="spinner" />
        <span className="ml-2 text-gray-600">Bewertungen werden geladen...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">
              Fehler beim Laden der Bewertungen
            </h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
            <div className="mt-4">
              <button
                type="button"
                className="bg-red-100 px-2 py-1 text-sm text-red-800 rounded hover:bg-red-200"
                onClick={() => {
                  clearError();
                  fetchReviews();
                }}
              >
                Erneut versuchen
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (reviews.length === 0) {
    return (
      <div className="text-center py-12">
        <svg
          className="mx-auto h-12 w-12 text-gray-400"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-1l-4 4z"
          />
        </svg>
        <h3 className="mt-2 text-sm font-medium text-gray-900">
          Keine Bewertungen vorhanden
        </h3>
        <p className="mt-1 text-sm text-gray-500">
          Seien Sie der erste, der eine Bewertung abgibt!
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filter Controls (nur wenn nicht embedded) */}
      {!embedded && (
        <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Bewertungen filtern</h3>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
            {/* Suchfeld */}
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                Suche
              </label>
              <input
                type="text"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Name oder Kommentar..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Bewertung filtern */}
            <div>
              <label htmlFor="rating" className="block text-sm font-medium text-gray-700 mb-1">
                Mindest-Bewertung
              </label>
              <select
                id="rating"
                value={selectedRating || ''}
                onChange={(e) => setSelectedRating(e.target.value ? parseInt(e.target.value) : undefined)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Alle Bewertungen</option>
                <option value="5">⭐⭐⭐⭐⭐ (5 Sterne)</option>
                <option value="4">⭐⭐⭐⭐ (4+ Sterne)</option>
                <option value="3">⭐⭐⭐ (3+ Sterne)</option>
                <option value="2">⭐⭐ (2+ Sterne)</option>
                <option value="1">⭐ (1+ Stern)</option>
              </select>
            </div>

            {/* Sortierung */}
            <div>
              <label htmlFor="sort" className="block text-sm font-medium text-gray-700 mb-1">
                Sortieren nach
              </label>
              <select
                id="sort"
                value={`${sortBy}-${sortOrder}`}
                onChange={(e) => {
                  const [by, order] = e.target.value.split('-');
                  setSortBy(by as 'created_at' | 'rating' | 'name');
                  setSortOrder(order as 'asc' | 'desc');
                }}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="created_at-desc">Neueste zuerst</option>
                <option value="created_at-asc">Älteste zuerst</option>
                <option value="rating-desc">Beste Bewertung zuerst</option>
                <option value="rating-asc">Schlechteste Bewertung zuerst</option>
                <option value="name-asc">Name A-Z</option>
                <option value="name-desc">Name Z-A</option>
              </select>
            </div>

            {/* Featured Only */}
            <div className="flex items-center">
              <div className="flex items-center h-5">
                <input
                  id="featured"
                  type="checkbox"
                  checked={showFeaturedOnly}
                  onChange={(e) => setShowFeaturedOnly(e.target.checked)}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                />
              </div>
              <div className="ml-3 text-sm">
                <label htmlFor="featured" className="font-medium text-gray-700">
                  Nur empfohlene Bewertungen
                </label>
              </div>
            </div>
          </div>

          {/* Filter-Buttons */}
          <div className="flex justify-between items-center">
            <div className="text-sm text-gray-500">
              {reviews.length} von {pagination.total} Bewertungen
            </div>
            <div className="flex space-x-3">
              <button
                onClick={resetFilters}
                className="px-4 py-2 text-sm text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                Filter zurücksetzen
              </button>
              <button
                onClick={handleFilterUpdate}
                className="px-4 py-2 text-sm text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                Filter anwenden
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Reviews */}
      <div className="space-y-4">
        {reviews.map((review) => (
          <div
            key={review.id}
            className={`review-card ${review.is_featured ? 'featured' : ''}`}
          >
            {/* Header */}
            <div className="flex items-start justify-between mb-3">
              <div>
                <h3 className="font-semibold text-gray-900">{review.name}</h3>
                {review.title && (
                  <h4 className="text-sm text-gray-600 mt-1">{review.title}</h4>
                )}
              </div>
              {review.is_featured && (
                <span className="badge badge-warning">Empfohlen</span>
              )}
            </div>

            {/* Rating */}
            <div className="mb-3">
              <RatingStars rating={review.rating} />
            </div>

            {/* Content */}
            <div className="text-gray-700 mb-4">
              <p className="whitespace-pre-wrap">
                {embedded 
                  ? apiUtils.truncateText(review.content, 150)
                  : review.content
                }
              </p>
            </div>

            {/* Image */}
            {review.image_path && (
              <div className="mb-4">
                <img
                  src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${review.image_path}`}
                  alt="Bewertungsbild"
                  className="max-w-full h-48 object-cover rounded-lg border border-gray-300"
                />
              </div>
            )}

            {/* Footer */}
            <div className="flex items-center justify-between text-sm text-gray-500 border-t border-gray-200 pt-3">
              <time dateTime={review.created_at}>
                {apiUtils.formatDate(review.created_at)}
              </time>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination (nur wenn nicht embedded) */}
      {!embedded && pagination.total_pages > 1 && (
        <Pagination
          currentPage={pagination.page}
          totalPages={pagination.total_pages}
          onPageChange={setPage}
        />
      )}

      {/* Loading overlay für zusätzliche Seiten */}
      {loading && reviews.length > 0 && (
        <div className="flex justify-center py-4">
          <div className="spinner" />
          <span className="ml-2 text-gray-600">Lädt...</span>
        </div>
      )}
    </div>
  );
};

export default ReviewList;
